import UIKit

protocol ManagerTabBarRouterInput {
    
}

protocol ManagerTabBarRouterDataSource: class {
    var currentUser: User? { get }
}

protocol ManagerTabBarRouterDataDestination: class {
    var currentUser: User? { get set }
}

class ManagerTabBarRouter: ManagerTabBarRouterInput {
    
    weak var viewController: ManagerTabBarViewController!
    weak private var dataSource: ManagerTabBarRouterDataSource!
    weak var dataDestination: ManagerTabBarRouterDataDestination!
    
    init(viewController: ManagerTabBarViewController, dataSource: ManagerTabBarRouterDataSource, dataDestination: ManagerTabBarRouterDataDestination) {
        self.viewController = viewController
        self.dataSource = dataSource
        self.dataDestination = dataDestination
    }
    
    // MARK: Navigation
    
    // MARK: Communication
    
    func passDataToChildTabs() {
        viewController.viewControllers?.forEach { child in
            if let navigationController = child as? UINavigationController,
               let appointmentsViewController = navigationController.viewControllers[0] as? AppointmentsViewController {
                appointmentsViewController.router?.dataDestination.user = dataSource.currentUser
            }
        }
    }
}
